import React, { useState } from 'react';
import { TextField, Button, Grid, Typography } from '@mui/material';
import DatePicker from '@mui/lab/DatePicker';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
// import { sendEmailVerification } from '/auth/signin';

export default function Signup() {
const [email, setEmail] = useState('');
const [password, setPassword] = useState('');
const [confirmPassword, setConfirmPassword] = useState('');
const [birthDate, setBirthDate] = useState(null);
const [name, setName] = useState('');
const [verificationSent, setVerificationSent] = useState(false);

const handleSubmit = (event) => {
    event.preventDefault();
    // 이메일 인증 이전까지는 이메일, 비밀번호, 생년월일, 이름 정보를 서버로 보내지 않음
    sendEmailVerification(email, password, birthDate, name);
    setVerificationSent(true);
};

const handleEmailChange = (event) => {
    setEmail(event.target.value);
};

const handlePasswordChange = (event) => {
    setPassword(event.target.value);
};

const handleConfirmPasswordChange = (event) => {
    setConfirmPassword(event.target.value);
};

const handleBirthDateChange = (date) => {
    setBirthDate(date);
};

const handleNameChange = (event) => {
    setName(event.target.value);
};

return (
    <form onSubmit={handleSubmit}>
    <Grid container spacing={2}>
        <Grid item xs={12}>
        <TextField
            required
            fullWidth
            id="email"
            label="이메일"
            name="email"
            value={email}
            onChange={handleEmailChange}
        />
        </Grid>
        <Grid item xs={12}>
        <TextField
            required
            fullWidth
            id="password"
            label="비밀번호"
            name="password"
            type="password"
            value={password}
            onChange={handlePasswordChange}
        />
        </Grid>
        <Grid item xs={12}>
        <TextField
            required
            fullWidth
            id="confirmPassword"
            label="비밀번호 확인"
            name="confirmPassword"
            type="password"
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
        />
        </Grid>
        <Grid item xs={12}>
        <LocalizationProvider dateAdapter={AdapterDateFns}>
            <DatePicker
            label="생년월일"
            value={birthDate}
            onChange={handleBirthDateChange}
            renderInput={(params) => <TextField {...params} />}
            />
        </LocalizationProvider>
        </Grid>
        <Grid item xs={12}>
        <TextField
            required
            fullWidth
            id="name"
            label="이름"
            name="name"
            value={name}
            onChange={handleNameChange}
        />
        </Grid>
    </Grid>
    <Button
        type="submit"
        variant="contained"
        color="primary"
        disabled={!email || !password || !confirmPassword || !birthDate || !name || password !== confirmPassword || verificationSent}
    >
        회원가입 완료
    </Button>
    </form>
);
}
